from sklearn.preprocessing import StandardScaler
from sklearn.ensemble import RandomForestClassifier
from sklearn.cluster import KMeans, DBSCAN
from sklearn.decomposition import PCA
from sklearn.metrics import silhouette_score
import matplotlib.pyplot as plt
import seaborn as sns
import pandas as pd
import numpy as np

# Remove Churn column for clustering
clustering_data = train_processed.drop(columns=['Churn'])

# Determine optimal number of clusters using Elbow Method and Silhouette Score
print("\n--- Finding Optimal K for KMeans ---")
inertia = []
silhouette_scores = []
k_range = range(2, 11)  # Testing K from 2 to 10
for k in k_range:
    kmeans = KMeans(n_clusters=k, random_state=42, n_init=10)
    kmeans_labels = kmeans.fit_predict(clustering_data)
    inertia.append(kmeans.inertia_)
    silhouette_scores.append(silhouette_score(clustering_data, kmeans_labels))

# Plot Elbow Method
plt.figure(figsize=(8, 5))
plt.plot(k_range, inertia, marker='o', linestyle='--')
plt.xlabel("Number of Clusters")
plt.ylabel("Inertia")
plt.title("Elbow Method for Optimal K")
plt.show()

# Plot Silhouette Scores
plt.figure(figsize=(8, 5))
plt.plot(k_range, silhouette_scores, marker='o', linestyle='--')
plt.xlabel("Number of Clusters")
plt.ylabel("Silhouette Score")
plt.title("Silhouette Score for Different K")
plt.show()

# Based on the Elbow Method and Silhouette Score, choose the optimal k
optimal_k = k_range[np.argmax(silhouette_scores)] # Replace with the value you find optimal
print(f"Optimal K chosen based on Silhouette Score: {optimal_k}")

# Perform K-Means clustering with the optimal k
kmeans = KMeans(n_clusters=optimal_k, random_state=42)
labels = kmeans.fit_predict(clustering_data)

# Analyze clusters
for cluster in range(optimal_k):
    cluster_data = train_processed[labels == cluster]
    print(f"\n--- Cluster {cluster} ---")
    print(cluster_data.describe())
    print("\nChurn Distribution:")
    print(cluster_data['Churn'].value_counts(normalize=True) * 100)

