from sklearn.cluster import DBSCAN
from sklearn.metrics import silhouette_score

# Ground truth labels for ARI calculation
clustering_data = train_processed.drop(columns=['Churn'])
# true_label = train_processed['Churn']  # Using churn as ground truth for ARI

silhouette, ari = [], []

best_silhouette_score = -1  # Silhouette ranges from -1 to 1
best_silhouette_params = None
best_silhouette_n_clusters = 0

for min_samples in np.arange(2, 20, 1):
    for eps in np.arange(0.1, 3.0, 0.2):

        # Run DBSCAN for the current parameter values
        dbscan = DBSCAN(eps=eps, min_samples=min_samples).fit(clustering_data)

        n_clusters = len(set(dbscan.labels_)) - (1 if -1 in dbscan.labels_ else 0)
        n_noise = list(dbscan.labels_).count(-1)

        # silhouette_score gives the average value for all the samples.
        # This gives a perspective into the density and separation of the formed clusters
        try:
            sil_score = silhouette_score(clustering_data, dbscan.labels_)
            silhouette.append((min_samples, eps, sil_score))
            if sil_score > best_silhouette_score:
                best_silhouette_score = sil_score
                best_silhouette_params = (min_samples, eps)
                best_silhouette_n_clusters = len(set(dbscan.labels_)) - (1 if -1 in dbscan.labels_ else 0)
        except Exception as e:
            pass

        # ari.append((min_samples, eps, adjusted_rand_score(true_label, dbscan.labels_)))

# Print the best parameters found
