best_model = optimized_models['XGBoost']  # Assuming XGBoost is the best model
best_model.fit(X_train_resampled, y_train_resampled)
y_scores = best_model.predict_proba(X_test)[:, 1]

# Calculate precision, recall, and F1 score for different thresholds
thresholds = np.arange(0.1, 1.0, 0.1)
threshold_results = []

for threshold in thresholds:
    y_pred_threshold = (y_scores >= threshold).astype(int)
    precision = precision_score(y_test, y_pred_threshold)
    recall = recall_score(y_test, y_pred_threshold)
    f1 = f1_score(y_test, y_pred_threshold)
    threshold_results.append((threshold, precision, recall, f1))

threshold_df = pd.DataFrame(threshold_results,
                           columns=['Threshold', 'Precision', 'Recall', 'F1 Score'])

# Find the threshold with the highest F1 score
best_threshold = threshold_df.loc[threshold_df['F1 Score'].idxmax(), 'Threshold']
print(f"Optimal threshold for F1 Score: {best_threshold}")

